import base64
from io import BytesIO

import PySimpleGUI as sg
from PIL import Image

PAUSE_BTN = b""
RECORD_BTN = b""
RESUME_BTN = b""
STOP_BTN = b""


def record_layout():
    buffer = BytesIO(base64.b64decode(sg.EMOJI_BASE64_HAPPY_BIG_SMILE))
    im1 = Image.open(buffer)
    with BytesIO() as output:
        im1.save(output, format="PNG")
        blank = output.getvalue()

    im2 = Image.new("RGBA", (50, 50), "#ffffff00")
    with BytesIO() as output:
        im2.save(output, format="PNG")
        blank = output.getvalue()

    record_layout = [
        [sg.Text("", key="record_status")],
        [
            sg.Text("Data Output File:"),
            sg.Input(key="record_file"),
            sg.SaveAs(
                "Select File",
                key="pick_record_file",
                file_types=(("PDF Files", "*.pdf"),),
                target="record_file",
            ),
        ],
        [
            sg.Spin(
                [i for i in range(5, 101, 5)],
                initial_value=10,
                size=(5, 1),
                key="record_interval",
            ),
            sg.Text("Data Interval (seconds)"),
        ],
        [
            sg.Push(),
            sg.pin(
                sg.Button(
                    image_data=RECORD_BTN,
                    button_color=(
                        sg.theme_background_color(),
                        sg.theme_background_color(),
                    ),
                    tooltip="Start Recording",
                    border_width=0,
                    key="start_recording",
                )
            ),
            sg.pin(
                sg.Button(
                    image_data=blank,
                    button_color=(
                        sg.theme_background_color(),
                        sg.theme_background_color(),
                    ),
                    border_width=0,
                    key="_placeholder",
                )
            ),
            sg.pin(
                sg.Button(
                    image_data=STOP_BTN,
                    tooltip="Stop Recording",
                    button_color=(
                        sg.theme_background_color(),
                        sg.theme_background_color(),
                    ),
                    border_width=0,
                    visible=False,
                    key="stop_recording",
                )
            ),
            sg.pin(
                sg.Button(
                    image_data=PAUSE_BTN,
                    tooltip="Pause Recording",
                    button_color=(
                        sg.theme_background_color(),
                        sg.theme_background_color(),
                    ),
                    border_width=0,
                    visible=False,
                    key="pause_recording",
                )
            ),
            sg.pin(
                sg.Button(
                    image_data=RESUME_BTN,
                    tooltip="Resume Recording",
                    button_color=(
                        sg.theme_background_color(),
                        sg.theme_background_color(),
                    ),
                    border_width=0,
                    visible=False,
                    key="resume_recording",
                )
            ),
            sg.Push(),
        ],
    ]
    return record_layout


def get_record_window():
    return sg.Window("Record Miner Data", record_layout(), modal=True)
